import React, { Component } from 'react';
import {ProductConsumer} from '../Context';
import {Link} from 'react-router-dom';
import {ButtonContainer, buttonContainer} from './Button';


class Details extends Component{
    render(){
        return(
        <ProductConsumer>
          {(value)=>{
            const {id, company, img, info,price, title, inCart} = value.detailProduct;
            return(
                <div className="container py-5">
                   {/* title */ }
                   <div className="row">

                      <div className="col-10 mx-auto text-center text-slanted text-blue my-5">
                          <h1>{title}</h1>
                      </div>

                   </div>
                   {/* end title */}

                   {/* produit info */}
                   
                   <div className="row">
                       {/* img */}
                      <div className="col-10 mx-auto col-md-6 my-3 text-capitalize">
                          <img src={img} className="img-fluid" alt="product"/>
                      </div>
                        {/* end img */}


                      <div className="col-10 mx-auto col-md-6 my-3 text-capitalize">
                         <h3>Model : {title}</h3>
                         <h4 className="text-title text-uppercase text-muted mt-3 mb-2"> Made by :<span className="text-uppercase">{company}</span></h4>
                         <h4 className="text-blue"><strong> Prix : {price} <span>€</span></strong></h4>
                         <p className="text-capitalize font-weight-bold mt-3 mb-0"> Informations sur le produit :</p>
                         <p className="text-muted lead">{info}</p>

                         {/* bouttons */}
                         <div>
                              <Link to="/">

                              <button type="button" class="btn btn-warning ml-3">
                              Retour
                              </button>
                                
                              </Link>
                              

                              <button type="button" class="btn btn-warning ml-3">
                              <span className="mr-1">+</span>
                              {inCart?" Dans panier" : "Ajouter au panier"}
                              </button>
                              


                              


                         </div>
                      </div>

                    </div>
                </div>
            )
          }}

        </ProductConsumer>


        );
    }
}
export default Details;
