import React, { Component } from 'react'
import {Link} from 'react-router-dom';

export default class CreerEnchere extends Component {

  constructor(props) {
    super(props);

    this.onChangeEtat = this.onChangeEtat.bind(this);
    this.onChangeNom = this.onChangeNom.bind(this);
    this.onChangeCategorie = this.onChangeCategorie.bind(this);
    this.onChangePrix = this.onChangePrix.bind(this);
    this.onChangeContact = this.onChangeContact.bind(this);
    this.onChangeInfo = this.onChangeInfo.bind(this);
    this.onChangeImg = this.onChangeImg.bind(this);
   

    this.state = {
     
      etat: '',
      nom: '',
      categorie:'',
      prix:'',
      contact:'',
      info:'',
      img:''
    };
  }

  onChangeEtat(e){
    this.setState({
      etat: e.target.value
    });
  }

  onChangeNom(e){
    this.setState({
      nom: e.target.value
    });
  }

  onChangeCategorie(e){
    this.setState({
      categorie: e.target.value
    });
  }

  onChangePrix(e){
    this.setState({
      prix: e.target.value
    });
  }

  onChangeContact(e){
    this.setState({
      contact: e.target.value
    });
  }

  onChangeInfo(e){
    this.setState({
      info: e.target.value
    });
  }

  onChangeImg(e){
    this.setState({
      img: e.target.value
    });
  }

  onSubmit(e){
    e.preventDefault();

  console.log(`Form submitted:`);
  console.log(`Etat: ${this.state.etat}`);
  console.log(`Nom: ${this.state.nom}`);
  console.log(`Categorie: ${this.state.categorie}`);
  console.log(`Prix: ${this.state.prix}`);
  console.log(`Contact: ${this.state.contact}`);
  console.log(`Info: ${this.state.info}`);
  console.log(`Img: ${this.state.img}`);

  this.setState({
    etat: '',
    nom: '',
    categorie:'',
    prix:'',
    contact:'',
    info:'',
    img:''
  })

  }


  render() {
    return (
      
      <div className="container py-3" >
      <h3 className="text-center mb-5">Créer une enchère</h3>
      <form onSubmit={this.onSubmit}>

      <div class="form-group text-blue col-md-3">
              <label>Etat du produit: </label>
              <input  type="text"
                      className="form-control"
                      value={this.state.etat}
                      onChange={this.onChangeEtat}
                      />
          </div>

          <div class="form-group text-blue col-md-3">
              <label>Nom du produit: </label>
              <input  type="text"
                      className="form-control"
                      value={this.state.nom}
                      onChange={this.onChangeNom}
                      />
          </div>


          <div class="form-group text-blue col-md-3">
              <label>Categorie: </label>
              <input  type="text"
                      className="form-control"
                      value={this.state.categorie}
                      onChange={this.onChangeCategorie}
                      />
          </div>


          <div className="form-group col-md-3">
              <label>Prix: </label>
              <input  type="text"
                      className="form-control"
                      value={this.state.prix}
                      onChange={this.onChangePrix}
                      />
          </div>


          <div className="form-group col-md-3">
              <label>Contact: </label>
              <input  type="text"
                      className="form-control"
                      value={this.state.contact}
                      onChange={this.onChangeContact}
                      />
          </div>

          <div className="form-group col-md-3">
              <label>Informations sur le produit: </label>
              <p className="text-muted lead col-md-6">
                      <textarea 
                      class="form-control"
                      rows="4"
                      value={this.state.info}
                      onChange={this.onChangeInfo}>
                      </textarea>
              </p>
          </div>

          <div className="form-group col-md-3">
              <label>Ajouter une image du produit: </label>
              <input  type="file"
                      className="form-control-file"
                      value={this.state.img}
                      onChange={this.onChangeImg}
                      />
          </div>
       

                 


                   <div className="col-md-6 ml-auto">
                      <Link to="/enchereList">
                        <button type="button" class="btn btn-primary  mt-3">
                           Annuler
                        </button>
                     </Link>
                              
                      <Link to="/cartE">
                        <button type="button" class="btn btn-warning ml-3 mt-3">
                          Valider la création
                        </button>
                      </Link>
                   </div>
                              


                              


       </form>
    </div>
                
            )
          }}
