import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import {Link} from 'react-router-dom';
import axios from 'axios';

export default class CreerEnchere extends Component {

    constructor(props) {
        super(props);

        this.onChangeTitleE = this.onChangeTitleE.bind(this);
        this.onChangeImgE = this.onChangeImgE.bind(this);
        this.onChangePriceE = this.onChangePrice.bind(this);
        this.onChangeIncE = this.onChangeIncE.bind(this);
        this.onChangeCompanyE = this.onChangeCompanyE.bind(this);
        this.onChangeInfoE = this.onChangeInfoE.bind(this);
       
        
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            titleE: '',
            imgE: '',
            priceE: '',
            incE: '',
            companyE: '',
            infoE: '',
            inPanE:false
        }
    }

    onChangeTitleE(e) {
        this.setState({
            titleE: e.target.value
        });
    }

    onChangeImgE(e) {
      this.setState({
          imgE: e.target.value
      });
  }

    onChangePriceE(e) {
        this.setState({
            priceE: e.target.value
        });
    }

    onChangeIncE(e) {
      this.setState({
          incE: e.target.value
      });
  }


  onChangeInPanE(e) {
    this.setState({
        inPanE: e.target.value
    }); 
    
}


onChangeCompanyE(e) {
  this.setState({
      companyE: e.target.value
  });

   
}


onChangeInfoE(e) {
  this.setState({
      infoE: e.target.value
  });

   
}

 




    onSubmit(e) {
      
      e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Etat: ${this.state.titleE}`);
        console.log(`Categorie: ${this.state.imgE}`);
        console.log(`Nom: ${this.state.priceE}`);
        console.log(`Prix: ${this.state.incE}`);
        console.log(`Info: ${this.state.companyE}`);
        console.log(`Contact: ${this.state.infoE}`);
        console.log(`Image: ${this.state.inPanE}`);


        const newEnchere ={

            titleE: this.state.titleE,
            imgE: this.state.imgE,
            priceE: this.state.priceE,
            incE: this.state.incE,
            companyE: this.state.companyE,
            infoE: this.state.infoE,
            inPanE: this.state.inPanE
        }

        axios.post('http://localhost:4000/EbayDB/add',newEnchere)
        .then(res => console.log(res.data));

        this.setState({
            titleE: '',
            imgE: '',
            priceE: '',
            incE: '',
            companyE: '',
            infoE: '',
            inPanE:false,
        });
    }

    render() {
        return (

          
            <div className="container   col-md-6" style={{marginTop: 20}}>
                <h3 className="text-center mt-2">Créer une enchère</h3>
                <form onSubmit={this.onSubmit} >

                    <div class="form-group text-blue col-md-6">
                        <label>Pour faire une offre : </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.incE}
                                onChange={this.onChangeIncE}
  
                               />
                    </div>

                    
                    <div class="form-group text-blue col-md-9">
                        <label>Titre: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.titleE}
                                onChange={this.onChangeTitleE}
                                />
                    </div>


                    <div class="form-group text-blue col-md-3">
                        <label>Prix (€): </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.priceE}
                                onChange={this.onChangePriceE}
                                />
                    </div>



                    <div class="form-group text-blue col-md-12  " >
                        <label>Détails: </label>
                        <textarea 
                                type="text" 
                                className="form-control"
                                value={this.state.infoE}
                                onChange={this.onChangeInfoE}
                                rows = "5"
                                
                                />
                    </div>


                    <div class="form-group text-blue col-md-3">
                        <label>Contact (tél): </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.companyE}
                                onChange={this.onChangeCompanyE}
  
                               />
                    </div>


                    <div class="form-group text-blue col-md-9">
                        <label>Image du produit: </label>
                        <input  type="file"
                                className="form-control"
                                value={this.state.imgE}
                                onChange={this.onChangeImgE}
  
                               />
                    </div>

                    

                    

                 




                   

                    <div className="form-group col-md-6 mt-2">
                        <Link to="/cartE">
                        <button className="btn btn-dark"> 
                        <span className="mr-2">
                        <i class="fas fa-chevron-left" />
                        </span>
                        Retour à mes enchere</button>
                        </Link>
                    </div>


                    
                    

                    <div className="form-group   mt-2 ml-2 ">
                    <input type="submit" value="Create Todo" className="btn btn-primary" />
                    </div>

                    
                    



                    
                    

                   

                    
                </form>
            </div>

            
        )
    }
}
