import React, { Component } from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {EncherConsumer} from '../Context';
import Timer from './TimerE';

export default class Enchere extends Component {
  
  render() {
    const {idE, titleE, imgE, priceE, inPanE, timeE } =this.props.enchere;
    return (
      <EnchereWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
        <div className="card">
        


       <div className="img-container p-5" onClick={() =>console.log('you cliked me on the img container')}>
       <Link to="/detailsE">
       <img src={imgE} alt="enchere" className="card-img-top" />
       </Link>

       <button className="cart-btn" disabled={inPanE?true:false} onClick={()=>{console.log('ajouter au panier des enchere');  }} >{inPanE?(<p className="text-capitalize" disabled>Enchere</p>):(<i className="fas fa-hand-paper" />) }</button>
       
       
      

       </div>
       </div>
      
      </EnchereWrapper>
    )
  }
}


const EnchereWrapper = styled.div`



`
