import React, { Component } from 'react';
import {storeProducts, detailProduct } from './data';
import {storeEncheres, detailEnchere } from './dataE';

const ProductContext = React.createContext();
//Provider
//Consumer

class ProductProvider extends Component{
  state={

    //product states
   products: [],
   detailProduct: detailProduct,
   cart : [],
   modalOpen : false,
   modalProduct : detailProduct,
   cartSubTotal :0,
   cartTax:0,
   cartTotal:0,

   //Enchere states

   encheres : [],
   detailEnchere : detailEnchere,
   panE : []


  };

  

  //Products methods

  setProducts = ()=>{
    let TempProducts = [];
    storeProducts.forEach(item =>{
      const singleItem = {...item};
      TempProducts = [...TempProducts,singleItem];
     })
  
     this.setState(() =>{
       return{products : TempProducts}
     })
  }

  getItem = (id) => {

    const product = this.state.products.find(item => item.id === id);
    return product;
  } 

  handleDetail = (id) => {
    const product = this.getItem(id);
    this.setState(()=>{

          return {detailProduct:product}
    })
  };

  addToCart = (id) => {
    let TempProducts = [...this.state.products];
    const index = TempProducts.indexOf(this.getItem(id));
    const product =TempProducts[index];
    product.inCart=true;
    product.count = 1;
    const price = product.price;
    product.total = price;
    this.setState(()=>{
      return{
        products:TempProducts, 
        cart :[...this.state.cart,product]
      };
    },()=>{
      this.addTotals();
    })
  }


  openModal = id =>{
      const product = this.getItem(id);
      this.setState(()=>{

        return{modalProduct:product,modalOpen:true}
      })
  }

  closeModal = ()=>{
    this.setState(()=>{
      return{modalOpen:false}
    })
  }

  increment = (id)=>{
    let tempCart = [...this.state.cart];
    const selectedProduct = tempCart.find(item=> item.id===id)

    const index = tempCart.indexOf(selectedProduct);
    const product = tempCart[index];

    product.count = product.count +1;
    product.total = product.count * product.price;

    this.setState(
                 ()=>{return{cart:[...tempCart]}},
                 ()=>{this.addTotals()})
  }

  decrement = (id)=>{
    let tempCart = [...this.state.cart];
    const selectedProduct = tempCart.find(item=> item.id===id)

    const index = tempCart.indexOf(selectedProduct);
    const product = tempCart[index];

    product.count = product.count -1;
    
    if(product.count===0){
      this.removeItem(id);
    }
    else{
      product.total = product.count * product.price;
      this.setState(
        ()=>{return{cart:[...tempCart]}},
        ()=>{this.addTotals();});
    }

  };

  removeItem = (id)=>{
    let TempProducts = [...this.state.products];
    let tempCart = [...this.state.cart];
    tempCart = tempCart.filter(item=> item.id !== id);
    const index = TempProducts.indexOf(this.getItem(id));
    let removedProduct = TempProducts[index];
    removedProduct.inCart=false;
    removedProduct.count= 0;
    removedProduct.total=0;

    this.setState(()=>{
      return{
        cart:[...tempCart],
        products:[...TempProducts]
      }
    },()=>{
      this.addTotals();
    })
  }

  clearCart = ()=>{
    this.setState(()=>{
      return{cart:[]};
    },()=>{
         this.setProducts();
         this.addTotals();
    });
  }


  addTotals = () =>{
    let subTotal = 0;
    this.state.cart.map(item=>(subTotal += item.total));
    const tempTax = subTotal * 0.2;
    const tax = parseFloat(tempTax.toFixed(2));
    const total = subTotal + tax;
    this.setState(()=>{
      return{
        cartSubTotal:subTotal,
        cartTax:tax,
        cartTotal:total

      }
    })
  }


  //Encheres methodes

  setEncheres = () =>{
     let tempEncheres =[];
     storeEncheres.forEach(item =>{
         const singleItem = {...item};
         tempEncheres = [...tempEncheres,singleItem]

     })
     this.setState(() =>{
       return{encheres:tempEncheres}
     })



  }


 getItemE = (idE) =>{
   const enchere = this.state.encheres.find(item => item.idE===idE);
   return enchere;

 };




  handleDetailE = (idE) =>{
   const enchere = this.getItemE(idE);
   this.setState(() =>{
     return {detailEnchere:enchere}
   })
  }

  addToPanE = (idE) =>{

    let tempEncheres = [...this.state.encheres];
    const index = tempEncheres.indexOf(this.getItem(idE));
    const enchere = tempEncheres[index];
    enchere.inPanE = true;
    const priceE = enchere.priceE;
    const incE = enchere.incE;
    this.setState(()=>{
      return {encheres:tempEncheres, panE:[...this.state.panE,enchere]};
    },()=>{console.log(this.state);
    });
  }; 





  //commun methodes

  componentDidMount(){
    this.setProducts();
    this.setEncheres();
  }

    render(){
        return(
           <ProductContext.Provider value={{

            //Products
            ...this.state,
            handleDetail: this.handleDetail,
            addToCart: this.addToCart,
            openModal: this.openModal,
            closeModal: this.closeModal,
            increment: this.increment,
            decrement: this.decrement,
            removeItem: this.removeItem,
            clearCart: this.clearCart,

            //Encheres

            handleDetailE : this.handleDetailE,
            addToPanE : this.addToPanE

          }}>
             {this.props.children}  
           </ProductContext.Provider>

        );
    }
}

const ProductConsumer = ProductContext.Consumer;

export {ProductProvider,ProductConsumer};
